import{_ as e,c as r,o as t,a}from"./app.1949cfb4.js";const f=JSON.parse('{"title":"\u4ECB\u7ECD","description":"","frontmatter":{},"headers":[{"level":2,"title":"wgpu \u662F\u4EC0\u4E48\uFF1F","slug":"wgpu-\u662F\u4EC0\u4E48\uFF1F","link":"#wgpu-\u662F\u4EC0\u4E48\uFF1F","children":[]},{"level":2,"title":"\u4E3A\u4EC0\u4E48\u4F7F\u7528 Rust\uFF1F","slug":"\u4E3A\u4EC0\u4E48\u4F7F\u7528-rust\uFF1F","link":"#\u4E3A\u4EC0\u4E48\u4F7F\u7528-rust\uFF1F","children":[]},{"level":2,"title":"\u8D21\u732E\u4E0E\u652F\u6301","slug":"\u8D21\u732E\u4E0E\u652F\u6301","link":"#\u8D21\u732E\u4E0E\u652F\u6301","children":[]},{"level":2,"title":"\u5173\u4E8E\u8BD1\u8005","slug":"\u5173\u4E8E\u8BD1\u8005","link":"#\u5173\u4E8E\u8BD1\u8005","children":[]}],"relativePath":"index.md","lastUpdated":1665458138000}'),n={name:"index.md"},s=a('<h1 id="\u4ECB\u7ECD" tabindex="-1">\u4ECB\u7ECD <a class="header-anchor" href="#\u4ECB\u7ECD" aria-hidden="true">#</a></h1><p><em>\u4E3A\u4E86\u4FBF\u4E8E\u8BFB\u8005\u7684\u7406\u89E3\uFF0C\u8BD1\u8005\u9009\u62E9\u6027\u7684\u6DFB\u52A0\u4E86\u4E00\u4E9B\u5185\u5BB9\uFF0C\u5E76\u5BF9\u539F\u6587\u4E2D\u6709\u6B67\u4E49\u6216\u9519\u8BEF\u7684\uFF08\u6BD4\u5982\uFF1A\u7B2C\u516B\u7AE0\u3001\u7B2C\u5341\u4E00\u7AE0 Srgb \u90E8\u5206\u7B49\u7B49\uFF09\u5730\u65B9\u8FDB\u884C\u91CD\u65B0\u8868\u8FF0\u3002\u6240\u6709\u7684\u6DFB\u52A0\u4E0E\u4FEE\u6539\u5747\u4E0D\u4F1A\u505A\u5355\u72EC\u6807\u8BB0\u3002</em></p><p><em>\u7FFB\u8BD1\u65F6\u91C7\u7528\u4E86\u7B2C\u4E00\u4EBA\u79F0\u89C6\u89D2\uFF0C\u6545\uFF0C\u9664\u4E86\u5E26 \u{1F195} \u6807\u8BB0\u7684\u7AE0\u8282\uFF0C\u6559\u7A0B\u4E2D\u7684 <strong>\u6211</strong> \u4E3B\u8981\u6307\u7684\u662F\u539F\u4F5C\u8005 <a href="https://github.com/sotrh" target="_blank" rel="noreferrer">@sorth</a>\u3002</em></p><p><em>\u53E6\u5916\uFF0C\u4E13\u6709\u540D\u8BCD\u5728\u4E00\u4E2A\u6BB5\u843D\u4E2D\u7B2C\u4E00\u6B21\u51FA\u73B0\u65F6\u505A\u4E86<strong>\u52A0\u7C97</strong>\u5904\u7406\uFF0C\u540C\u4E00\u6BB5\u843D\u91CC\u53CD\u590D\u51FA\u73B0\u65F6\u5C31\u4E0D\u518D\u52A0\u7C97\u3002</em></p><h2 id="wgpu-\u662F\u4EC0\u4E48\uFF1F" tabindex="-1">wgpu \u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#wgpu-\u662F\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><p><a href="https://github.com/gfx-rs/wgpu" target="_blank" rel="noreferrer">wgpu</a> \u662F\u57FA\u4E8E <a href="https://gpuweb.github.io/gpuweb/" target="_blank" rel="noreferrer">WebGPU API \u89C4\u8303</a> \u7684\u3001\u8DE8\u5E73\u53F0\u7684\u3001\u5B89\u5168\u7684\u3001\u7EAF Rust \u56FE\u5F62 API\u3002\u5B83\u662F Firefox\u3001Servo \u548C Deno \u4E2D WebGPU \u6574\u5408\u7684\u6838\u5FC3\u3002</p><p><strong>wgpu</strong> \u4E0D\u4EC5\u53EF\u4EE5\u5728 Web \u73AF\u5883\u8FD0\u884C\uFF0C\u8FD8\u53EF\u4EE5\u5728 macOS / iOS\u3001Android\u3001Window \u548C Linux \u7B49\u7CFB\u7EDF\u4E0A\u539F\u751F\u8FD0\u884C\u3002</p><p><strong>WebGPU</strong> \u662F\u7531 W3C <a href="https://www.w3.org/community/gpu/" target="_blank" rel="noreferrer">GPU for the Web</a> \u793E\u533A\u7EC4\u6240\u53D1\u5E03\u7684\u89C4\u8303\uFF0C\u76EE\u6807\u662F\u5141\u8BB8\u7F51\u9875\u4EE3\u7801\u4EE5\u9AD8\u6027\u80FD\u4E14\u5B89\u5168\u53EF\u9760\u7684\u65B9\u5F0F\u8BBF\u95EE GPU \u529F\u80FD\u3002\u5B83\u901A\u8FC7\u501F\u9274 Vulkan API\uFF0C\u5E76\u5C06\u5176\u8F6C\u6362\u4E3A\u5BBF\u4E3B\u786C\u4EF6\u4E0A\u4F7F\u7528\u7684\u5404\u5F0F API\uFF08\u5982 DirectX\u3001Metal\u3001Vulkan\uFF09\u6765\u5B9E\u73B0\u8FD9\u4E00\u76EE\u6807\u3002</p><p><em>WebGPU \u89C4\u8303\u53CA wgpu \u90FD\u4ECD\u5728\u5B8C\u5584\u9636\u6BB5\uFF0C\u672C\u6559\u7A0B\u4E2D\u7684\u90E8\u5206\u5185\u5BB9\u53EF\u80FD\u4F1A\u53D1\u751F\u53D8\u5316\u3002\u5982\u679C\u6709\u9519\u8FC7\u4E00\u4E9B\u91CD\u8981\u7684\u7EC6\u8282\uFF0C\u6216\u8005\u89E3\u91CA\u5F97\u4E0D\u591F\u597D\uFF0C\u6B22\u8FCE\u63D0\u51FA\u5EFA\u8BBE\u6027\u7684\u53CD\u9988\u3002</em></p><h2 id="\u4E3A\u4EC0\u4E48\u4F7F\u7528-rust\uFF1F" tabindex="-1">\u4E3A\u4EC0\u4E48\u4F7F\u7528 Rust\uFF1F <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4F7F\u7528-rust\uFF1F" aria-hidden="true">#</a></h2><p>wgpu \u5B9E\u9645\u4E0A\u63D0\u4F9B\u4E86 C \u8BED\u8A00\u7ED1\u5B9A (<a href="https://github.com/gfx-rs/wgpu-native" target="_blank" rel="noreferrer">wgpu-native</a>)\uFF0C\u4F60\u53EF\u4EE5\u5199 C/C++ \u6216\u5176\u4ED6\u80FD\u4E0E C \u4E92\u901A\u7684\u8BED\u8A00\u6765\u4F7F\u7528\u5B83\u3002\u5C3D\u7BA1\u5982\u6B64\uFF0Cwgpu \u672C\u8EAB\u662F\u7528 Rust \u5B9E\u73B0\u7684\uFF0C\u5B83\u4FBF\u5229\u7684 Rust \u7ED1\u5B9A\u80FD\u51CF\u5C11\u4F60\u4F7F\u7528\u4E2D\u7684\u963B\u788D\u3002\u66F4\u91CD\u8981\u7684\u662F\uFF0CRust \u662F\u4E00\u95E8\u9AD8\u6027\u80FD\uFF0C\u5185\u5B58\u548C\u7EBF\u7A0B\u5B89\u5168\u4E14\u6781\u5177\u751F\u4EA7\u529B\u7684\u73B0\u4EE3\u5E95\u5C42\u8BED\u8A00\u3002</p><p>\u5728\u5B66\u4E60\u672C\u6559\u7A0B\u4E4B\u524D\u4F60\u9700\u8981\u5148\u719F\u6089 Rust\uFF0C\u56E0\u4E3A\u8FD9\u91CC\u4E0D\u4F1A\u8BE6\u7EC6\u4ECB\u7ECD Rust \u7684\u8BED\u6CD5\u77E5\u8BC6\u3002\u5982\u679C\u5BF9 Rust \u8FD8\u4E0D\u592A\u719F\u6089\uFF0C\u53EF\u4EE5\u56DE\u987E\u4E00\u4E0B <a href="https://www.rust-lang.org/zh-CN/learn" target="_blank" rel="noreferrer">Rust \u6559\u7A0B</a>\u6216 <a href="https://course.rs/about-book.html" target="_blank" rel="noreferrer">Rust \u8BED\u8A00\u5723\u7ECF</a>\u3002\u53E6\u5916\u8FD8\u9700\u8981\u719F\u6089 Rust \u5305\u7BA1\u7406\u5DE5\u5177 <a href="https://doc.rust-lang.org/cargo" target="_blank" rel="noreferrer">Cargo</a>\u3002</p><div class="note"><p>\u5982\u679C\u4F60\u8FD8\u6CA1\u6709\u5B66\u4E60\u8FC7\u4EFB\u4F55\u4E00\u95E8\u5E95\u5C42\u8BED\u8A00\uFF0C\u90A3\u5C31\u5B66 Rust \u5427\uFF0C\u5B83\u4E0D\u50CF C++ \u6709\u90A3\u4E48\u591A\u590D\u6742\u96BE\u61C2\u7684\u6982\u5FF5;</p><p>\u5982\u679C\u4F60\u89C9\u5F97\u5B66 C/C++ \u5F88\u96BE\uFF0C\u90A3\u5C31\u5B66 Rust \u5427\uFF0C\u4F60\u4F1A\u53D7\u76CA\u4E8E\u5B83\u7684\u9AD8\u6027\u80FD\uFF0C\u5185\u5B58\u548C\u7EBF\u7A0B\u5B89\u5168;</p><p>\u5982\u679C\u4F60\u5DF2\u7CBE\u901A C/C++\uFF0C\u90A3\u4E5F\u5B66\u5B66 Rust \u5427\uFF0C\u90A3\u4E48\u96BE\u7684\u90FD\u5B66\u5F97\u4F1A\uFF0C\u638C\u63E1 Rust \u4E0D\u8FC7\u662F\u5C0F\u83DC\u4E00\u789F;</p></div><h2 id="\u8D21\u732E\u4E0E\u652F\u6301" tabindex="-1">\u8D21\u732E\u4E0E\u652F\u6301 <a class="header-anchor" href="#\u8D21\u732E\u4E0E\u652F\u6301" aria-hidden="true">#</a></h2><ul><li>\u539F\u4F5C\u8005\u63A5\u53D7 PR\uFF08<a href="https://github.com/sotrh/learn-wgpu" target="_blank" rel="noreferrer">GitHub repo</a>\uFF09\u4EE5\u4FEE\u590D\u672C\u6559\u7A0B\u7684\u95EE\u9898\uFF0C\u5982\u9519\u522B\u5B57\u3001\u9519\u8BEF\u4FE1\u606F\u548C\u5176\u4ED6\u4E0D\u4E00\u81F4\u4E4B\u5904\u3002</li><li>\u5982\u679C\u60F3\u76F4\u63A5\u652F\u6301\u539F\u4F5C\u8005\uFF0C\u8BF7\u67E5\u770B <a href="https://www.patreon.com/sotrh" target="_blank" rel="noreferrer">patreon</a>\uFF01</li><li>\u4E5F\u6B22\u8FCE\u5BF9\u6B64\u4E2D\u8BD1\u7248\u672C\u63D0\u51FA\u53CD\u9988\u53CA\u6539\u8FDB\u5EFA\u8BAE\uFF08<a href="https://github.com/jinleili/learn-wgpu-zh" target="_blank" rel="noreferrer">\u300ALearn wgpu\u300B\u4E2D\u6587\u7248 repo</a>\uFF09</li></ul><h2 id="\u5173\u4E8E\u8BD1\u8005" tabindex="-1">\u5173\u4E8E\u8BD1\u8005 <a class="header-anchor" href="#\u5173\u4E8E\u8BD1\u8005" aria-hidden="true">#</a></h2><p>\u6211\u662F\u4E00\u540D\u79FB\u52A8\u7AEF\uFF08iOS\uFF0CAndroid\uFF09\u5DE5\u7A0B\u5E08, \u540C\u65F6\u4E5F\u64C5\u957F HTML5\uFF0C\u6709\u591A\u5E74\u4F7F\u7528 OpenGL ES / WebGL, Metal \u7684\u5B9E\u8DF5\u7ECF\u9A8C\u30022018 \u5E74\u5F00\u59CB\u63A5\u89E6 WebGPU\uFF0C\u76EE\u524D\u6B63\u79EF\u6781\u5730\u53C2\u4E0E\u5230 <a href="https://github.com/gfx-rs/wgpu/commits?author=jinleili" target="_blank" rel="noreferrer">wgpu \u5F00\u6E90\u9879\u76EE\u7684\u5F00\u53D1\u4E0E\u5B8C\u5584</a>\u4E4B\u4E2D\uFF0C\u5E76\u4E14\u5DF2\u4E8E\u4E24\u5E74\u524D\u5728 AppStore \u4E0A\u67B6\u4E86\u57FA\u4E8E wgpu \u5B9E\u73B0\u7684\u6BDB\u7B14\u4E66\u6CD5\u6A21\u62DF App <a href="https://apps.apple.com/cn/app/%E5%AD%97%E4%B9%A0-pro/id1507339788" target="_blank" rel="noreferrer">\u5B57\u4E60 Pro</a>\u3002</p>',17),p=[s];function o(l,i,h,u,g,d){return t(),r("div",null,p)}const _=e(n,[["render",o]]);export{f as __pageData,_ as default};
