import{_ as s,c as n,o as r,a as e}from"./app.b1ed28ac.js";const a="/learn-wgpu-zh/assets/connect.317eedac.jpg",o="/learn-wgpu-zh/assets/realtime-left.fceb4675.jpg",t="/learn-wgpu-zh/assets/realtime.9dd2da17.jpg",p="/learn-wgpu-zh/assets/trace.b6c48e7e.png",l="/learn-wgpu-zh/assets/GMEM_load.3c3ed86f.jpg",d="/learn-wgpu-zh/assets/GMEM_store.6f15c0e3.jpg",i="/learn-wgpu-zh/assets/frame.5d75a9c3.jpg",c="/learn-wgpu-zh/assets/resource-left.1d783628.jpg",g="/learn-wgpu-zh/assets/resource-right.c404daaa.jpg",G=JSON.parse('{"title":"\u4F7F\u7528 Snapdragon Profiler \u8C03\u8BD5 wgpu \u7A0B\u5E8F","description":"","frontmatter":{},"headers":[],"relativePath":"integration-and-debugging/snapdragon-profiler/index.md","lastUpdated":1669193974000}'),u={name:"integration-and-debugging/snapdragon-profiler/index.md"},h=e('<h1 id="\u4F7F\u7528-snapdragon-profiler-\u8C03\u8BD5-wgpu-\u7A0B\u5E8F" tabindex="-1">\u4F7F\u7528 Snapdragon Profiler \u8C03\u8BD5 wgpu \u7A0B\u5E8F <a class="header-anchor" href="#\u4F7F\u7528-snapdragon-profiler-\u8C03\u8BD5-wgpu-\u7A0B\u5E8F" aria-hidden="true">#</a></h1><p>\u5728<a href="./../android/">\u4E0E Android App \u96C6\u6210</a>\u7AE0\u8282\u6211\u4EEC\u5DF2\u7ECF\u5B66\u4E60\u4E86 wgpu \u4E0E Android App \u7684\u96C6\u6210\uFF0C\u73B0\u5728\u6765\u770B\u770B\u96C6\u6210\u540E\u7684\u8C03\u8BD5\u3002</p><h2 id="snapdragon-profiler-\u5DE5\u5177\u4ECB\u7ECD" tabindex="-1">Snapdragon Profiler \u5DE5\u5177\u4ECB\u7ECD <a class="header-anchor" href="#snapdragon-profiler-\u5DE5\u5177\u4ECB\u7ECD" aria-hidden="true">#</a></h2><p><a href="https://developer.qualcomm.com/software/snapdragon-profiler" target="_blank" rel="noreferrer">Snapdragon Profiler</a> \u662F\u9AD8\u901A\u516C\u53F8\u5F00\u53D1\u7684\u4E00\u6B3E\u53EF\u8FD0\u884C\u5728 Windows\u3001Mac \u548C Linux \u5E73\u53F0\u4E0A\u7684\u6027\u80FD\u5206\u6790\u548C\u5E27\u8C03\u8BD5\u5DE5\u5177\u3002 \u5B83\u901A\u8FC7 USB \u4E0E<strong>\u5B89\u5353\u8BBE\u5907</strong>\u8FDE\u63A5\uFF0C\u5141\u8BB8\u5F00\u53D1\u4EBA\u5458\u5206\u6790 CPU\u3001GPU\u3001\u5185\u5B58\u7B49\u6570\u636E\uFF0C\u4EE5\u4FBF\u6211\u4EEC\u53D1\u73B0\u5E76\u4FEE\u590D\u6027\u80FD\u74F6\u9888\u3002</p><p>Snapdragon Profiler \u5DE5\u5177\u7684\u529F\u80FD\u7279\u70B9\uFF1A</p><ul><li>\u5B9E\u65F6\u76D1\u6D4B GPU \u6027\u80FD;</li><li>\u67E5\u770B CPU \u8C03\u5EA6\u548C GPU \u9636\u6BB5\u6570\u636E\uFF0C\u4E86\u89E3\u5E94\u7528\u7A0B\u5E8F\u5C06\u65F6\u95F4\u82B1\u5728\u54EA\u91CC;</li><li>GPU <strong>\u5E27</strong>\u6355\u83B7;</li><li>\u5355\u6B65\u8C03\u8BD5<strong>\u5E27</strong>\u7ED8\u5236;</li><li>\u67E5\u770B\u548C\u7F16\u8F91<strong>\u7740\u8272\u5668</strong>\u5E76\u5728\u8BBE\u5907\u4E0A\u9884\u89C8\u7ED3\u679C;</li><li>\u67E5\u770B\u548C\u8C03\u8BD5\u50CF\u7D20\u5386\u53F2\u8BB0\u5F55;</li><li>\u6355\u83B7\u548C\u67E5\u770B\u6BCF\u6B21\u7ED8\u5236\u8C03\u7528\u7684 GPU \u6307\u6807;</li></ul><p>\u4E0A\u9762\u7684\u5B98\u7F51\u94FE\u63A5\u63D0\u4F9B\u4E86\u5BF9\u5E94\u5E73\u53F0\u5B89\u88C5\u5305\u7684\u514D\u8D39\u4E0B\u8F7D\u3002\u5982\u679C\u662F Mac \u548C Linux \u5E73\u53F0, \u5728\u5B89\u88C5 Snapdragon Profiler \u4E4B\u524D\u9700\u8981\u5148\u5B89\u88C5 <a href="http://www.mono-project.com/download/" target="_blank" rel="noreferrer">momo \u6846\u67B6</a>\uFF08mono \u662F Windows .Net \u6846\u67B6\u7684\u8DE8\u5E73\u53F0\u5F00\u6E90\u5B9E\u73B0\uFF09\u3002 \u5728\u8FD0\u884C Snapdragon Profiler \u4E4B\u524D\u9700\u8981\u786E\u4FDD\u7CFB\u7EDF\u4E0A\u5B89\u88C5\u4E86 Android Studio \u6216\u8005 AndroidSDK\uFF0C\u5E76\u4E14\u5DF2\u5C06 <strong>ADB</strong> \u8DEF\u5F84\u6DFB\u52A0\u5230\u7CFB\u7EDF\u73AF\u5883\u53D8\u91CF\u4E2D\u3002</p><h2 id="\u5B9E\u65F6\u6A21\u5F0F\u67E5\u770B-gpu-\u7EDF\u8BA1\u6570\u636E" tabindex="-1">\u5B9E\u65F6\u6A21\u5F0F\u67E5\u770B GPU \u7EDF\u8BA1\u6570\u636E <a class="header-anchor" href="#\u5B9E\u65F6\u6A21\u5F0F\u67E5\u770B-gpu-\u7EDF\u8BA1\u6570\u636E" aria-hidden="true">#</a></h2><p>USB \u8FDE\u63A5\u8981\u8C03\u8BD5\u7684 Android \u624B\u673A\u540E\u6253\u5F00 Snapdragon Profiler\uFF0C\u70B9\u51FB\u7A97\u53E3\u5DE6\u8FB9\u680F\u7684 <strong>Start a Session</strong>, \u6B64\u65F6\u53F3\u8FB9\u51FA\u73B0\u7684\u5C0F\u5F39\u7A97\u91CC\u4F1A\u5217\u51FA\u5F53\u524D\u4E0E\u7535\u8111\u8FDE\u63A5\u7684\u6240\u6709\u53EF\u8C03\u8BD5\u8BBE\u5907\uFF0C\u6211\u4EEC\u9009\u4E2D\u5217\u8868\u4E2D\u7684\u8BBE\u5907\uFF0C\u52FE\u9009\u4E0A\u5F39\u7A97\u5DE6\u4E0B\u89D2\u7684 <strong>Auto connect</strong> \u518D\u70B9\u51FB\u53F3\u4E0B\u89D2\u7684 <strong>Connect</strong>\uFF0C\u8FD9\u6837\uFF0C\u4E0B\u56DE\u518D\u6B21\u8C03\u8BD5\u540C\u4E00\u53F0\u8BBE\u5907\u65F6\u5C31\u80FD\u81EA\u52A8\u8FDE\u63A5\u5230 Snapdragon Profiler \u4E86\uFF1A</p><img src="'+a+'"><p>\u8FDE\u63A5\u540E\uFF0C\u6709\u56DB\u79CD\u8C03\u8BD5\u6A21\u5F0F\u4F9B\u6211\u4EEC\u9009\u62E9\uFF1A\u5B9E\u65F6\u3001\u8FFD\u8E2A\u3001\u5E27\u6355\u83B7\u53CA CPU \u91C7\u6837\uFF0C\u73B0\u5728\u9009\u62E9<strong>\u5B9E\u65F6</strong>\uFF08\u5DE6\u56FE\uFF09\uFF0C\u5728<strong>\u5B9E\u65F6</strong>\u7A97\u53E3\u7684\u5DE6\u8FB9\u680F\u5C55\u793A\u4E86\u5B9E\u65F6\u6307\u6807\u5217\u8868\uFF0C\u6211\u4EEC\u53EF\u4EE5\u9009\u62E9\u76F8\u5E94\u7684\u6307\u6807\u9879\u6765\u6536\u96C6 CPU\u3001GPU\u3001\u5185\u5B58\u3001\u7F51\u7EDC\u3001\u7535\u6E90\u548C\u6563\u70ED\u7684\u5B9E\u65F6\u6307\u6807\uFF08\u53F3\u56FE\uFF09\uFF1A</p><div style="display:flex;"><div><img src="'+o+'" alt="\u5B9E\u65F6\u6A21\u5F0F"></div><div style="width:20px;"></div><div><img src="'+t+`" alt="\u5B9E\u65F6\u9884\u89C8"></div></div><p>\u4E0A\u9762\u7684\u53F3\u56FE\u4E2D\uFF0C\u6211\u9009\u62E9\u4E86 <strong>GPU General</strong>\u3001<strong>GPU Stalls</strong> \u4E24\u4E2A\u6307\u6807\u7C7B\u522B\uFF0C\u7A97\u53E3\u53F3\u8FB9\u5C55\u793A\u4E86\u6BCF\u4E2A\u7EC6\u5206\u6307\u6807\u7684\u5B9E\u65F6\u6570\u636E\u56FE\u8868\uFF0C\u8981\u6DFB\u52A0\u65B0\u7684\u6307\u6807\u56FE\u8868\uFF0C\u53EA\u9700\u53CC\u51FB\u7C7B\u522B\uFF08\u4EE5\u6DFB\u52A0\u7C7B\u522B\u4E2D\u7684\u6240\u6709\u6307\u6807\uFF09\u6216\u5355\u4E2A\u6307\u6807\uFF0C\u6216\u8005\u5C06\u7C7B\u522B\u6216\u6307\u6807\u62D6\u653E\u5230\u53F3\u4FA7\u7684\u201C\u56FE\u8868\u201D\u7A97\u683C\u4E2D\u3002</p><h2 id="\u8FFD\u8E2A\u6A21\u5F0F\u68C0\u67E5\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" tabindex="-1">\u8FFD\u8E2A\u6A21\u5F0F\u68C0\u67E5\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D <a class="header-anchor" href="#\u8FFD\u8E2A\u6A21\u5F0F\u68C0\u67E5\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" aria-hidden="true">#</a></h2><p><strong>\u7247\u4E0A\u5185\u5B58</strong>\uFF08on-chip memory\uFF09\u88C5\u8F7D\u662F\u5F71\u54CD\u79FB\u52A8\u5E94\u7528\u4E2D GPU \u6027\u80FD\u7684\u5E38\u89C1\u95EE\u9898\u4E4B\u4E00\u3002\u5728\u672C\u8282\u4E2D\uFF0C\u6211\u4EEC\u6765\u5B66\u4E60\u5982\u4F55\u4F7F\u7528 Snapdragon Profiler \u67E5\u627E\u548C\u5B9A\u4F4D\u5F15\u8D77\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\u7684\u5E94\u7528\u7A0B\u5E8F\u4EE3\u7801\u3002</p><div class="note"><p>Snapdragon Profiler \u91CC\u5C06<strong>\u7247\u4E0A\u5185\u5B58</strong>\u79F0\u4E4B\u4E3A<strong>\u56FE\u5F62\u5185\u5B58</strong>\uFF08GMEM\uFF0C\u5168\u79F0 Graphic Memory\uFF09\uFF0C\u4F46\u662F\u8FD9\u91CC\u7684\u56FE\u5F62\u5185\u5B58\u8DDF\u663E\u5B58\u5BB9\u6613\u6DF7\u6DC6\uFF0C\u5B83\u4FE9\u5E76\u4E0D\u662F\u4E00\u56DE\u4E8B\u3002\u6545\uFF0C\u4E0B\u8FB9\u7EDF\u4E00\u4F7F\u7528<strong>\u7247\u4E0A\u5185\u5B58</strong>\u6765\u6307\u4EE3 GMEM\u3002</p></div><h3 id="\u4EC0\u4E48\u662F\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" tabindex="-1">\u4EC0\u4E48\u662F\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" aria-hidden="true">#</a></h3><p>\u79FB\u52A8 GPU \u7684 <strong>Tiling</strong> \u67B6\u6784\u7BA1\u7EBF\u5305\u62EC\u4E00\u4E2A\u6E32\u67D3\u901A\u9053\u3002\u5728\u6E32\u67D3\u8FC7\u7A0B\u4E2D\uFF0C\u6BCF\u4E2A <strong>Tile</strong> \u90FD\u662F\u5148\u88AB\u6E32\u67D3\u5230<strong>\u7247\u4E0A\u5185\u5B58</strong>\u4E2D\u3002\u6309\u7167\u9A71\u52A8\u7A0B\u5E8F\u7684\u9ED8\u8BA4\u884C\u4E3A\uFF0C\u5148\u524D\u7684<strong>\u5E27\u7F13\u51B2\u533A</strong>\u6570\u636E\u88AB\u4ECE\u8BBE\u5907\u5185\u5B58\u52A0\u8F7D\u5230\u6BCF\u4E2A Tile \u7684\u7247\u4E0A\u5185\u5B58\u4E2D\uFF0C\u5373\u53D1\u751F\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\u3002</p><div class="note"><p>\u6240\u8C13 Tiling\uFF0C\u672C\u8D28\u4E0A\u5C31\u662F\u7BA1\u7406 GPU \u5185\u5B58\u7684\u6280\u672F\u3002Tiling \u5229\u7528<strong>\u7247\u4E0A\u5185\u5B58</strong>\uFF08on-chip memory\uFF09\u53BB\u964D\u4F4E<strong>\u8BBE\u5907\u5185\u5B58</strong>\u7684\u8BBF\u95EE\u6B21\u6570\uFF0C\u4ECE\u800C\u964D\u4F4E GPU \u5185\u5B58\u5E26\u5BBD\u7684\u6D88\u8017\u53CA\u8BBF\u95EE\u5EF6\u8FDF\u3002 \u6B63\u786E\u7406\u89E3\u5E76\u5229\u7528 Tiling \u67B6\u6784\u7684\u5185\u5B58\u7BA1\u7406\u7279\u6027\uFF0C\u53EF\u4EE5\u6709\u6548\u7684\u63D0\u9AD8 GPU \u7A0B\u5E8F\u7684\u6027\u80FD\u3002</p></div><h3 id="\u4E3A\u4EC0\u4E48\u8981\u5C3D\u53EF\u80FD\u5730\u51CF\u5C11\u6216\u907F\u514D\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" tabindex="-1">\u4E3A\u4EC0\u4E48\u8981\u5C3D\u53EF\u80FD\u5730\u51CF\u5C11\u6216\u907F\u514D\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\uFF1F <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8981\u5C3D\u53EF\u80FD\u5730\u51CF\u5C11\u6216\u907F\u514D\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" aria-hidden="true">#</a></h3><p>\u56E0\u4E3A\u6BCF\u4E00\u6B21<strong>\u7247\u4E0A\u5185\u5B58</strong>\u7684\u52A0\u8F7D\u90FD\u4F1A\u51CF\u6162 GPU \u7684\u5904\u7406\u901F\u5EA6\u3002<br> \u5982\u679C\u5728 <code>begin_render_pass</code> \u65F6\u901A\u8FC7\u8BBE\u7F6E <code>Clear()</code> \u6765\u6E05\u7406\u7247\u4E0A\u5185\u5B58\uFF0C\u9A71\u52A8\u7A0B\u5E8F\u5C31\u53EF\u4EE5\u907F\u514D\u5728\u7247\u4E0A\u5185\u5B58\u4E2D\u88C5\u8F7D<strong>\u5E27\u7F13\u51B2\u533A</strong>\u6570\u636E\u3002\u867D\u7136\u8FD9\u6D89\u53CA\u5230\u4E00\u4E2A\u989D\u5916\u7684\u56FE\u5F62\u6307\u4EE4\u8C03\u7528\u53CA\u5176\u76F8\u5173\u7684\u5F00\u9500\uFF0C\u4F46\u5B83\u6BD4\u4E3A\u6BCF\u4E2A\u6B63\u5728\u6E32\u67D3\u7684 <strong>Tile</strong> \u5C06\u5E27\u7F13\u51B2\u533A\u6570\u636E\u52A0\u8F7D\u56DE\u7247\u4E0A\u5185\u5B58\u7684\u5F00\u9500\u8981\u4F4E\u5F97\u591A\u3002</p><p>\u5BFC\u81F4<strong>\u7247\u4E0A\u5185\u5B58</strong>\u88C5\u8F7D\u7684\u6700\u4E3B\u8981\u539F\u56E0\u662F: \u5BF9\u9A71\u52A8\u7A0B\u5E8F\u7684\u4E0D\u6070\u5F53\u63D0\u793A\u3002 \u5E94\u7528\u7A0B\u5E8F\u4EE3\u7801\u4F7F\u9A71\u52A8\u7A0B\u5E8F\u8BA4\u4E3A\u9700\u8981<strong>\u5E27\u7F13\u51B2\u533A</strong>\u7684\u5148\u524D\u5185\u5BB9\u3002</p><h3 id="\u68C0\u6D4B\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" tabindex="-1">\u68C0\u6D4B\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D <a class="header-anchor" href="#\u68C0\u6D4B\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D" aria-hidden="true">#</a></h3><p>\u5728 Snapdragon Profiler \u7684<strong>\u8FFD\u8E2A\u6A21\u5F0F</strong>\u4E0B\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA9<strong>\u6E32\u67D3\u9636\u6BB5</strong>\uFF08Rendering Stages\uFF09 \u6307\u6807\u7A81\u51FA\u663E\u793A\u5176\u81EA\u8EAB\u901A\u9053\u4E2D\u7684<strong>\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D</strong>\uFF08GMEM Loads\uFF09\u3002</p><div class="note"><p>GPU \u5E94\u7528\u5FC5\u987B\u5728\u9879\u76EE\u7684 AndroidManifest.xml \u6587\u4EF6\u4E2D\u5305\u542B <code>INTERNET</code> \u6743\u9650\u4EE5\u542F\u7528\u56FE\u5F62 API \u53CA GPU \u6307\u6807\u7684\u8FFD\u8E2A\uFF1A</p><div class="language-toml line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">toml</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">&lt;uses-permission android:name=&quot;android.permission.INTERNET&quot; /&gt;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u53E6\u5916\uFF0CSnapdragon Profiler \u7684\u8FFD\u8E2A\u6A21\u5F0F\u4E0D\u5141\u8BB8\u8FFD\u8E2A\u6355\u83B7\u8D85\u8FC7 10 \u79D2\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4ECE\u70B9\u51FB <code>Start Capture</code> \u5F00\u59CB\u5230\u70B9\u51FB <code>Stop Capture</code> \u7ED3\u675F\uFF0C\u65F6\u957F\u4E0D\u5F97\u8D85\u8FC7 10 \u79D2\u3002</p></div><p>\u542F\u7528<strong>\u8FFD\u8E2A\u6A21\u5F0F</strong>\u7684\u64CD\u4F5C\u6B65\u9AA4\uFF1A</p><ul><li>\u8FDE\u63A5\u597D Android \u8BBE\u5907\u540E\uFF0C\u4ECE <code>Start Page</code> \u754C\u9762\u5355\u51FB\u5DE6\u8FB9\u680F\u7684 <code>System Trace Analysis</code>\uFF0C\u6B64\u65F6\uFF0C\u5C31\u521B\u5EFA\u4E86\u4E00\u4E2A\u65B0\u7684 <code>Trace</code> \u9009\u9879\u5361\u3002</li><li>\u9009\u62E9\u521A\u521B\u5EFA\u7684 <code>Trace</code> \u9009\u9879\u5361\uFF0C\u8FDB\u5165\u4E00\u4E2A\u7C7B\u4F3C\u4E8E<strong>\u5B9E\u65F6</strong>\u6A21\u5F0F\u7684\u89C6\u56FE\uFF0C\u7136\u540E\u5728 <code>Data Sources</code> \u8FB9\u680F\u4E0A\u7AEF\u7684\u5E94\u7528\u5217\u8868\u4E2D\u9009\u4E2D\u8981\u8FFD\u8E2A\u7684\u5E94\u7528\uFF08\u5982\u679C\u5217\u8868\u4E2D\u627E\u4E0D\u5230\uFF0C\u5C31\u901A\u8FC7\u5217\u8868\u53F3\u4E0A\u89D2\u7684 <code>Launch</code> \u6309\u94AE\u53BB\u542F\u52A8\u8981\u8FFD\u8E2A\u7684\u5E94\u7528\uFF09\u3002</li><li>\u5728 <code>Data Sources</code> \u8FB9\u680F\u4E0B\u7AEF\uFF0C\u9009\u4E2D <code>Process</code> -&gt; <code>Vulkan</code> -&gt; <code>Rendering Stages</code> \u9879\u3002</li></ul><img src="`+p+'" style="max-width:425px;"><p>\u70B9\u51FB <code>Start Capture</code> \u5F00\u59CB\u8FFD\u8E2A\uFF0C\u5728 10 \u79D2\u5185\u7684\u4EFB\u610F\u6BB5\u70B9\u51FB <code>Stop Capture</code>\uFF0C\u5728\u7B49\u5F85 N \u79D2\uFF08\u53D6\u51B3\u4E8E\u7535\u8111\u6027\u80FD\uFF09\u540E\u5C31\u4F1A\u5C55\u793A\u51FA\u5982\u4E0B\u56FE\u8868\uFF1A</p><img src="'+l+`"><p>\u4E0A\u56FE\u6E32\u67D3\u9636\u6BB5\u7684<strong>\u8BBE\u7F6E</strong>\u5BF9\u8BDD\u6846\u663E\u793A\uFF0C\u8FD9\u4E9B<strong>\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D</strong>\u6D88\u8017\u4E86\u603B\u6E32\u67D3\u65F6\u95F4\u7684 23% \u5DE6\u53F3\u3002</p><p>\u6211\u4EEC\u6765\u770B\u770B\u6E90\u7801\u5E27\u6E32\u67D3\u4E2D\u7684<a href="https://github.com/jinleili/wgpu-on-app/blob/88e53957f7c80dbd8e75273c9ff48ecab958984f/src/examples/cube.rs#L356-L363" target="_blank" rel="noreferrer">\u8FD9\u6761 begin_render_pass() \u547D\u4EE4</a>\uFF0C\u989C\u8272\u9644\u4EF6\u7684\u7247\u4E0A\u64CD\u4F5C\u4F7F\u7528\u4E86 Load\uFF1A</p><div class="language-rust line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">rust</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">ops</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">wgpu</span><span style="color:#89DDFF;">::</span><span style="color:#FFCB6B;">Operations</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#676E95;">    // load: wgpu::LoadOp::Clear(wgpu::Color::BLACK),</span></span>
<span class="line"><span style="color:#A6ACCD;">    load</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">wgpu</span><span style="color:#89DDFF;">::</span><span style="color:#FFCB6B;">LoadOp</span><span style="color:#89DDFF;">::</span><span style="color:#FFCB6B;">Load</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    store</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">true,</span></span>
<span class="line"><span style="color:#89DDFF;">},</span><span style="color:#A6ACCD;">                    </span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>\u4F46\u6B64\u5904\u5B9E\u9645\u4E0A\u6CA1\u6709\u88C5\u8F7D\u4E4B\u524D\u7684\u5E27\u7F13\u51B2\u533A\u6570\u636E\u7684\u5FC5\u8981\uFF0C\u6211\u4EEC\u6539\u4E3A\u4F7F\u7528 <code>Clear()</code> \u6539\u5584\u6027\u80FD\u4E4B\u540E\uFF0C\u5C31\u56DE\u6536\u4E86\u4E4B\u524D\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\u6D88\u8017\u7684\u6027\u80FD\uFF0C\u4E0B\u56FE\u53EF\u4EE5\u770B\u5230 <code>GMEM Load</code> \u7EDF\u8BA1\u9879\u6D88\u5931\u4E86\uFF08\u6CA1\u6709\u53D1\u751F\u7247\u4E0A\u5185\u5B58\u88C5\u8F7D\u65F6\u5C31\u4E0D\u4F1A\u663E\u793A\uFF09\uFF1A</p><img src="`+d+'"><h2 id="\u5E27\u6355\u83B7\u6A21\u5F0F" tabindex="-1">\u5E27\u6355\u83B7\u6A21\u5F0F <a class="header-anchor" href="#\u5E27\u6355\u83B7\u6A21\u5F0F" aria-hidden="true">#</a></h2><p><strong>\u5E27\u6355\u83B7\u6A21\u5F0F</strong>\u5141\u8BB8\u6355\u83B7 GPU \u5E94\u7528\u7A0B\u5E8F\u7684\u5355\u4E00\u5E27, \u53EF\u4EE5\u8BE6\u7EC6\u663E\u793A\u4E00\u4E2A\u573A\u666F\u5728 GPU \u4E0A\u7684\u6E32\u67D3\u60C5\u51B5\u3002</p><p>\u542F\u7528\u5E27\u6355\u83B7\u6A21\u5F0F\u7684\u64CD\u4F5C\u4E0E\u8FFD\u8E2A\u6A21\u5F0F\u51E0\u4E4E\u4E00\u6837\uFF0C\u552F\u4E00\u4E0D\u540C\u4E4B\u5904\u5C31\u662F\u5E27\u6355\u83B7\u6A21\u5F0F\u5728\u70B9\u51FB <code>Take Snapshot</code> \u6355\u83B7\u4E00\u5E27\u6570\u636E\u540E\u4F1A\u81EA\u52A8\u7ED3\u675F\u6355\u83B7\uFF1A</p><img src="'+i+'"><p>\u5DE6\u4FA7<span style="color:red;">\u7EA2\u6846</span>\u533A\u57DF\u662F\u5F53\u524D\u5E27\u7684\u7740\u8272\u5668\u4EE3\u7801\uFF0C\u5B83\u4EEC\u662F\u7531 WGSL \u81EA\u52A8\u8F6C\u6362\u800C\u6765\u7684 SPIR-V \u4EE3\u7801\uFF08\u5F53\u7136\uFF0C\u6B64\u5904\u7684\u7740\u8272\u5668\u4EE3\u7801\u8FD8\u53D6\u51B3\u4E8E GPU \u5E94\u7528\u6240\u4F7F\u7528\u7684\u56FE\u5F62\u540E\u7AEF\uFF0C\u6211\u4F7F\u7528\u7684\u662F Vulkan \u540E\u7AEF\uFF0C\u5982\u679C\u4F7F\u7528 OpenGL \u540E\u7AEF\uFF0C\u6B64\u5904\u5C31\u4F1A\u663E\u793A GLSL \u4EE3\u7801\uFF09\u3002\u7EA2\u6846\u4E0B\u65B9\u7684\u533A\u57DF\u53EF\u4EE5\u663E\u793A\u7740\u8272\u5668\u7684\u9519\u8BEF\u4FE1\u606F\u3002\u8BF4\u5230\u8FD9\u91CC\u5C31\u4E0D\u5F97\u4E0D\u63D0 WebGPU \u7684 WGSL \u7740\u8272\u5668\u8BED\u8A00\u7684\u4F18\u52BF\u4E86\uFF1AWGSL \u5728\u7F16\u8BD1\u9636\u6BB5\u65F6\u5C31\u5F97\u5230\u4E86\u5F88\u597D\u7684\u9A8C\u8BC1\uFF0C\u8FD0\u884C\u65F6\u7684\u9A8C\u8BC1\u66F4\u662F\u80FD\u65B9\u4FBF\u5730\u6307\u51FA\u7740\u8272\u5668\u4E0E\u7BA1\u7EBF\u4E0D\u4E00\u81F4\u7684\u5730\u65B9\u3002\u6240\u4EE5\uFF0C\u6211\u4EEC\u4E0D\u9700\u8981\u4F9D\u8D56 Snapdragon Profiler \u7684\u7740\u8272\u5668\u8C03\u8BD5\u529F\u80FD\u3002</p><p>\u4E2D\u95F4<span style="color:green;">\u7EFF\u6846</span>\u533A\u57DF\u662F<strong>\u547D\u4EE4\u961F\u5217</strong>\uFF08Queue\uFF09\u63D0\u4EA4\u7ED9\u5F53\u524D\u5E27\u7684\u6240\u6709 Vulkan \u547D\u4EE4\u3002\u9009\u4E2D\u67D0\u4E00\u6761\u547D\u4EE4\uFF0C\u53F3\u4FA7\u8D44\u6E90\u9762\u677F\u5C06\u5C55\u793A\u51FA\u6B64\u547D\u4EE4\u6D89\u53CA\u7684\u6240\u6709\u8D44\u6E90\uFF1A\u56FE\u5F62|\u8BA1\u7B97\u7BA1\u7EBF\uFF0C\u7EB9\u7406\uFF0C\u7740\u8272\u5668\u7B49\u7B49\u3002</p><p>\u53F3\u4FA7<span style="color:blue;">\u84DD\u6846</span>\u533A\u57DF\u662F\u8D44\u6E90\u9762\u677F\u3002\u9009\u4E2D\u67D0\u4E00\u9879\u8D44\u6E90\uFF0C\u4E0B\u65B9\u7684\u9762\u677F\u5C06\u80FD\u5C55\u793A\u51FA\u8D44\u6E90\u8BE6\u60C5\u3002<br> \u6BD4\u5982\uFF0C\u9009\u62E9\u7EB9\u7406\u8D44\u6E90\u540E\uFF0C\u4E0B\u65B9\u7684 <code>Image Preview</code> \u9009\u9879\u5361\u4F1A\u5C55\u793A\u53EF\u7F29\u653E\u7684\u5927\u56FE\u9884\u89C8\uFF0C\u9F20\u6807\u5728\u7EB9\u7406\u56FE\u7247\u4E0A\u6ED1\u52A8\u53EF\u663E\u793A\u5BF9\u5E94\u50CF\u7D20\u7684 RGB \u8272\u503C\uFF0C<code>Inspector</code> \u9009\u9879\u5361\u4F1A\u5C55\u793A\u7EB9\u7406\u7684\u683C\u5F0F\u53CA\u5C42\u6B21\u7EC6\u8282\u53C2\u6570\u7B49\uFF08\u5DE6\u56FE\uFF09; \u9009\u62E9\u5E03\u5C40\u63CF\u8FF0\u7B26\u8D44\u6E90\u540E\uFF0C<code>Inspector</code> \u9009\u9879\u5361\u4F1A\u5C55\u793A\u51FA<strong>\u7ED1\u5B9A\u7EC4\u5E03\u5C40\u63CF\u8FF0\u7B26</strong>\uFF08BindGroupLayoutDescriptor\uFF09\u8BE6\u60C5\uFF08\u53F3\u56FE\uFF09\uFF1A</p><div style="display:flex;"><div><img src="'+c+'"></div><div style="width:20px;"></div><div><img src="'+g+'"></div></div>',43),m=[h];function f(_,b,P,y,C,S){return r(),n("div",null,m)}const A=s(u,[["render",f]]);export{G as __pageData,A as default};
